import React, { useState } from 'react';
import LoginForm from './components/LoginForm';
import SignupForm from './components/SignupForm';

function App() {
  const [activeTab, setActiveTab] = useState('login');

  const handleSocialLogin = (provider) => {
    console.log(`Social login with ${provider}`);
    alert(`${provider} login would be implemented here`);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50 flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Ubuy</h1>
          <p className="text-gray-600">Welcome back! Please enter your details.</p>
        </div>

        {/* Auth Container */}
        <div className="bg-white rounded-2xl shadow-soft p-8">
          {/* Tabs */}
          <div className="flex mb-8 border-b border-gray-200">
            <button
              onClick={() => setActiveTab('login')}
              className={`tab-button ${activeTab === 'login' ? 'active' : 'inactive'}`}
            >
              Log In
            </button>
            <button
              onClick={() => setActiveTab('signup')}
              className={`tab-button ${activeTab === 'signup' ? 'active' : 'inactive'}`}
            >
              Sign Up
            </button>
          </div>

          {/* Form Content */}
          <div className="min-h-[400px]">
            {activeTab === 'login' ? (
              <LoginForm
                onSwitchToSignup={() => setActiveTab('signup')}
                onSocialLogin={handleSocialLogin}
              />
            ) : (
              <SignupForm
                onSwitchToLogin={() => setActiveTab('login')}
                onSocialLogin={handleSocialLogin}
              />
            )}
          </div>
        </div>

        {/* Footer */}
        <div className="text-center mt-6">
          <p className="text-sm text-gray-500">
            By continuing, you agree to our{' '}
            <a href="/terms" className="text-primary-600 hover:text-primary-700 underline">
              Terms of Service
            </a>{' '}
            and{' '}
            <a href="/privacy" className="text-primary-600 hover:text-primary-700 underline">
              Privacy Policy
            </a>
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
