import React, { useState } from 'react';
import LoginForm from './components/LoginForm';
import SignupForm from './components/SignupForm';
import useDarkMode from './components/Mode';

function App() {
  const [activeTab, setActiveTab] = useState('login');
  const [isDark, setIsDark] = useDarkMode();

  const handleSocialLogin = (provider) => {
    console.log(`Social login with ${provider}`);
    alert(`${provider} login would be implemented here`);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50 dark:from-gray-900 dark:via-gray-800 dark:to-black flex items-center justify-center p-4 transition-colors duration-300">
      <div className="w-full max-w-md">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex justify-center items-center mb-2">
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Ubuy</h1>

            
          </div>
          <p className="text-gray-600 dark:text-gray-300">
            Welcome back! Please enter your details.
          </p>
        </div>

        {/* Auth Container */}
        <div className="bg-white dark:bg-gray-800 dark:text-white rounded-2xl shadow-soft p-8 transition-colors duration-300">
          {/* Tabs */}
          <div className="flex mb-8 border-b border-gray-200 dark:border-gray-700">
            <button
              onClick={() => setActiveTab('login')}
              className={`tab-button ${activeTab === 'login' ? 'active' : 'inactive'}`}
            >
              Log In
            </button>
            <button
              onClick={() => setActiveTab('signup')}
              className={`tab-button ${activeTab === 'signup' ? 'active' : 'inactive'}`}
            >
              Sign Up
            </button>
          </div>

          {/* Form Content */}
          <div className="min-h-[400px]">
            {activeTab === 'login' ? (
              <LoginForm
                onSwitchToSignup={() => setActiveTab('signup')}
                onSocialLogin={handleSocialLogin}
              />
            ) : (
              <SignupForm
                onSwitchToLogin={() => setActiveTab('login')}
                onSocialLogin={handleSocialLogin}
              />
            )}
          </div>
        </div>

        {/* Footer */}
        <div className="text-center mt-6">
          <p className="text-sm text-gray-500 dark:text-gray-400">
            By continuing, you agree to our{' '}
            <a href="/terms" className="text-primary-600 hover:text-primary-700 underline">
              Terms of Service
            </a>{' '}
            and{' '}
            <a href="/privacy" className="text-primary-600 hover:text-primary-700 underline">
              Privacy Policy
            </a>
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
